function passwordMask(){
;(function($){$.fn.showPassword=function(ph,options){var spinput=$(this);$.fn.showPassword.checker=function(cbid,inid){$('input[id="'+cbid+'"]').click(function(){if($(this).attr('checked')){$('input.'+inid).val(spinput.val()).attr('id',spinput.attr('id')).attr('name',spinput.attr('name'));$('input.'+inid).css('display','inline');spinput.css('display','none').removeAttr('id').removeAttr('name');}else{spinput.val($('input.'+inid).val()).attr('id',$('input.'+inid).attr('id')).attr('name',$('input.'+inid).attr('name'));spinput.css('display','inline');$('input.'+inid).css('display','none').removeAttr('id').removeAttr('name');}});}
return this.each(function(){var def={classname:'class',name:'password-input',text:'Unmask password?'};var spcbid='spcb';var spinid=spcbid.replace('spcb','spin');var spclass=spinid;if(typeof ph=='object'){$.extend(def,ph);}if(typeof options=='object'){$.extend(def,options);}var spname=def.name;if(def.classname==''){theclass='';}else{theclass=' class="'+def.clasname+'"';}$(this).before('<input type="text" value="" class="'+spclass+'" style="display: none;" />');var thecheckbox='<input type="checkbox" id="'+spcbid+'" name="'+spname+'" value="sp" /> <label for="'+spcbid+'">'+def.text+'</label>';if(ph=='object'||typeof ph=='undefined'){$(this).after(thecheckbox);}else{$(ph).html(thecheckbox);}$.fn.showPassword.checker(spcbid,spinid);return this;});}})(jQuery);
};
